func Elevator(directions string) (int, int) {
	directionsArray := strings.Split(directions, "")
	floor, position := 0, 0

	for i, v := range directionsArray {
		if v == "(" {
			floor = floor + 1
		} else {
			floor = floor - 1
		}
		if floor == -1 && position == 0 {
			position = i + 1
		}
	}
	return position, floor
}
